import { h } from '@stencil/core';
/**
 * Provide messaging to clarify that the data/stream has invalid DataType(s)
 */
export const UnsupportedDataTypeStatus = ({ supportedDataTypes, messageOverrides, size, }) => {
    const { width, height, marginLeft, marginRight, marginTop, marginBottom } = size;
    return (h("div", { id: "unsupported-data-type-error", class: "unsupported-data-type-status", style: {
            width: `${width + marginLeft + marginRight}px`,
            height: `${height + marginBottom + marginTop}px`,
            padding: '24px',
            background: 'white',
            border: 'solid 4px black',
        } },
        h("h2", null, messageOverrides.unsupportedDataTypeHeader || 'Unable to render your data'),
        h("h2", null, messageOverrides.unsupportedDataTypeSubHeader || 'This chart only supports the following DataType(s):'),
        h("div", null, messageOverrides.supportedTypes || supportedDataTypes.map(type => h("div", null, type)))));
};
