import { h } from '@stencil/core';
import { round } from '../../utils/number';
import { NO_VALUE_PRESENT } from '../common/terms';
/**
 * Display value of a data point, supports all data types
 */
export const Value = ({ isEnabled = true, value, unit }) => {
    if (!isEnabled || value == null) {
        return h("span", { "data-testid": "no-value-present" }, NO_VALUE_PRESENT);
    }
    if (typeof value === 'number') {
        /** Display Number */
        return [round(value), unit && h("span", { class: "unit" },
                " ",
                unit)];
    }
    /** Display String or Booleans */
    return [String(value), unit && h("span", { class: "unit" },
            " ",
            unit)];
};
